site.headStyle:
@media (min-width:481px) {
  div.paged-element-visible.column-section {
    display: flex;
    position: relative;
    top: auto;
    left: auto;
  }

  div.column-section>.silex-element-content {
    display: none;
  }

  div.column-section>.editable-style {
    flex: 1 1 auto;
    position: relative;
    top: auto;
    left: auto;
    width: auto;
    height: auto;
    min-height: 20px;
    margin: 15px;
    transition: margin .2s ease;
  }

  div.editable-style.drop-zone-candidate>.editable-style {
    margin-bottom: 55px;
  }

  .silex-runtime div.column-section>.editable-style {
    margin: 0;
  }

  div.column-section>.editable-style>.editable-style {
    position: relative;
    top: auto;
    left: auto;
    margin: auto;
  }
}
